        private void btnKhoa_Click(object sender, EventArgs e)
        {
            int rowIndex = gvDanhMuc.FocusedRowHandle;
            string colID = "Account_ID";
            object value = gvDanhMuc.GetRowCellValue(rowIndex, colID);
            if (value != null)
            {
                int Id = (int)value;
                if (Id != 0)
                {
                    DataFitnessCenterDataContext db = new DataFitnessCenterDataContext();
                    var account = db.Accounts.Where(i => i.Account_ID == (int)Id).SingleOrDefault();
                    if (account != null)
                    {
                        String status = Convert.ToString(account.Account_Status);
                        if (status != "Đang hoạt động")
                        {
                            status = "Đang hoạt động";
                            BLL_Human.UpdateAccountStatus(Id, status);
                            XtraMessageBox.Show("Đã mở hoạt động tài khoản này", "Fitness Center", MessageBoxButtons.OK, MessageBoxIcon.Information);

                        }
                        else
                        {
                            status = "Đã bị khóa";
                            BLL_Human.UpdateAccountStatus(Id, status);
                            XtraMessageBox.Show("Đã khóa tài khoản này", "Fitness Center", MessageBoxButtons.OK, MessageBoxIcon.Warning);
                        }
                        sqlDataSource1.Fill();
                    }
                }
            }
            else
            {
                XtraMessageBox.Show("Bạn chưa chọn đối tượng để khóa", "Fitness Center", MessageBoxButtons.OK, MessageBoxIcon.Warning);
            }
        }

        private void btnLamMoi_Click(object sender, EventArgs e)
        {
            sqlDataSource1.Fill();
        }
        private Form IsExits(Type type)
        {
            foreach (Form f in this.MdiChildren)
            {
                if (f.GetType() == type)
                {
                    return f;
                }
            }
            return null;
        }

        private void btnThem_Click(object sender, EventArgs e)
        {
            Form frm = this.IsExits(typeof(AccountDetail));
            if (frm != null)
            {
                frm.Activate();
            }
            else
            {
                AccountDetail account = new AccountDetail();
                account.isAdd = true;
                account.ShowDialog();
                sqlDataSource1.Fill();
            }
        }

        private void csmXoa_Click(object sender, EventArgs e)
        {
            try
            {
                if (XtraMessageBox.Show("Bạn có muốn xóa không?", "Fitness Center", MessageBoxButtons.YesNo, MessageBoxIcon.Question) == DialogResult.Yes)
                {
                    int rowIndex = gvDanhMuc.FocusedRowHandle;
                    string colID = "Account_ID";
                    object value = gvDanhMuc.GetRowCellValue(rowIndex, colID);
                    if (value != null)
                    {
                        DataFitnessCenterDataContext dc = new DataFitnessCenterDataContext();
                        var account = dc.Accounts.Where(s => s.Account_ID == (int)value).SingleOrDefault();
                        int accountId = Convert.ToInt32(account.Account_ID);
                        if (Id != accountId)
                        {
                            if (account != null)
                            {
                                dc.Accounts.DeleteOnSubmit(account);
                                dc.SubmitChanges();
                                sqlDataSource1.Fill();            
                            }
                        }
                        else
                        {
                            XtraMessageBox.Show("Không được phép xóa tài khoản sử dụng hiện tại", "Fitness Center", MessageBoxButtons.OK, MessageBoxIcon.Warning);
                        }
                    }
                    else
                    {
                        XtraMessageBox.Show("Bạn chưa chọn đối tượng cần xóa", "Fitness Center", MessageBoxButtons.OK, MessageBoxIcon.Warning);
                    }
                }
            }
            catch (Exception)
            {
                XtraMessageBox.Show("Không được phép xóa đối tượng này, đối tượng đã được thêm ở một danh mục khác", "Fitness Center", MessageBoxButtons.OK, MessageBoxIcon.Error);
            }
        }

        private void csmCapNhat_Click(object sender, EventArgs e)
        {
            int rowIndex = gvDanhMuc.FocusedRowHandle;
            string colID = "Account_ID";
            object value = gvDanhMuc.GetRowCellValue(rowIndex, colID);
            if (value != null)
            {
                AccountDetail account = new AccountDetail();
                account.Id = (int)value;
                account.isAdd = false;
                account.ShowDialog();
                sqlDataSource1.Fill();
            }
            else
            {
                XtraMessageBox.Show("Bạn chưa chọn đối tượng để cập nhật", "Fitness Center", MessageBoxButtons.OK, MessageBoxIcon.Warning);
            }
        }
    }
